     ARGB[1] = R0;
     ARGB[2] = G0;
     ARGB[3] = B0;
     ARGB[4] = A1;
     ARGB[5] = R1;
     ARGB[6] = G1;
     ARGB[7] = B1;
     
     uint8_t *destPixel = dest.data;
     destPixel[0] = ARGB[permuteMap[0]];
     destPixel[1] = ARGB[permuteMap[1]];
     destPixel[2] = ARGB[permuteMap[2]];
     destPixel[3] = ARGB[permuteMap[3]];
     destPixel[4] = ARGB[permuteMap[0]+4];
